import java.math.BigInteger;
import java.util.Stack;

/**
 * Questions:
 */
public class RemoveKDigits {
    public static void main(String[] args) {
        System.out.println(removeKdigits("1432219", 3) + " should be [1219].");
        System.out.println(removeKdigits("10200", 1) + " should be [200].");
        System.out.println(removeKdigits("10", 2) + " should be [0].");
//        System.out.println(removeKdigits("", 1000) + " should be [0].");
        System.out.println("********************* Elegant *********************");
        System.out.println(removeKdigits_elegant("1432219", 3) + " should be [1219].");
        System.out.println(removeKdigits_elegant("10200", 1) + " should be [200].");
        System.out.println(removeKdigits_elegant("10", 2) + " should be [0].");
        System.out.println(removeKdigits_elegant("", 1000) + " should be [0].");
    }

    public static String removeKdigits_elegant(String num, int k) {
        if (num == null || num.length() == k) return "0";
        Stack<Character> stack = new Stack<>();
//        Loop from the left of num and find out K elements that can be removed.
        for (int i = 0; i < num.length(); i++) {
//            If top most element in stack is greater than the current element.
            while (k > 0 && !stack.isEmpty() && stack.peek() > num.charAt(i)) {
//                Then remove the top element  from stack. And reduce the k value.
                stack.pop();
                k--;
            }
            stack.add(num.charAt(i));
        }
//        If all the elements are same. Then remove last K elements.
        while (k > 0) {
            stack.pop();
            k--;
        }
//        Collect all the values into a string.
        StringBuilder sb = new StringBuilder();
        while (!stack.isEmpty()) {
            sb.append(stack.pop());
        }
//        Remove all the last chars that have zero.
        while (sb.length() > 0 && sb.charAt(sb.length() - 1) == '0') {
            sb.deleteCharAt(sb.length() - 1);
        }
        return sb.length() == 0 ? "0" : sb.reverse().toString();
    }

    public static String removeKdigits(String num, int k) {
        int rows = k + 1, cols = num.length() + 1;
        String[][] dp = new String[rows][cols];
//        Initialize the first row, for each element when no value is removed.
        String cur = "";
        for (int i = 1; i < cols; i++) {
            cur += num.charAt(i - 1);
            dp[0][i] = cur;
        }
        for (int i = 0; i < rows; i++) {
            dp[i][0] = "";
        }
        for (int row = 1; row < rows; row++) {
            for (int col = 1; col < cols; col++) {
                String excludingCurrent = dp[row - 1][col - 1];
                String includingCurrent = dp[row][col - 1] + num.charAt(col - 1);
                dp[row][col] = min(excludingCurrent, includingCurrent);
            }
        }
        return dp[rows - 1][cols - 1].length() == 0 ? "0" : dp[rows - 1][cols - 1];
    }

    private static String min(String a, String b) {
        if (a.length() < b.length()) return a;
        if (a.length() > b.length()) return b;
//        boolean aIsBigger = true;
//        int index = 0;
//        while (index < a.length()) {
//            if (a.charAt(index) < b.charAt(index)) {
//                aIsBigger = false;
//                break;
//            }
//            index++;
//        }
//        return aIsBigger ? b : a;
        if (a.equals("") && b.equals("")) return "";
        BigInteger aBig = new BigInteger(a);
        BigInteger bBig = new BigInteger(b);
        return aBig.compareTo(bBig) > 0 ? bBig.toString() : aBig.toString();
    }
}
